import React, { useState, useEffect } from 'react';
import "./User_account.css";
import DefaultUser from './default-user.png';
import ArrrowBack from './back.png';
import EditPencil from './pencil.png';
import { Link } from "react-router-dom";
import axios from "axios";
import useToken from './Auth-components/App/useToken';

let name_user = "User";
let surname = "Super";
let nickname = "ProGamer";
let age = 17;
let score = 1234;

const getUserData = () => {
  const { token } = useToken();
  const headers = {
    Authorization: `Bearer ${token}`,
  };

  return axios.get("https://your-server.com/api/users/me", { headers })
    .then((response) => {
      const data = JSON.parse(response.data);

      //get user`s data from server
      const name_user = data.name_user;
      const surname = data.surname;
      const nickname = data.nickname;
      const age = data.age;
      const score = data.score;

      // Update the UI with the fetched data
      setName(name_user);
      setSurname(surname);
      setNickname(nickname);
      setAge(age);
      setScore(score);
    })
    .catch((error) => {
      console.log(error);
    });
};

export default function Account() {
  const { token } = useToken();

  const [name, setName] = useState(name_user);
  const [surname, setSurname] = useState(surname);
  const [nickname, setNickname] = useState(nickname);
  const [age, setAge] = useState(age);
  const [score, setScore] = useState(score);

  useEffect(() => {
    getUserData();
  }, []);

  const handleClick = () => {
    // Створіть нове поле введення.
    const input = document.createElement("input");
    input.type = "text";
    input.name = "nickname";

    // Додайте поле введення до батьківського елемента кнопки.
    const container = document.querySelector(".User_data_Nick_edit");
    container.appendChild(input);

    // Зосередьте поле введення.
    input.focus();
  };

  return (
    <div className='Account'>
      <h1 className='Hello_user'>Hello, {name}!</h1>
      <div className='User_data'>
        <button type="back" className="User_data_back">
          <Link to="/">
            <img src={ArrrowBack} alt="back" className="User_data_back" />
          </Link>
        </button>
        <img src={DefaultUser} alt="DefUser" className="User_data_DefaultUser" />
        <User_data name={name} surname={surname} nickname={nickname} age={age} score={score} />
      </div>
    </div>
  );
}

const User_data = ({ name, surname, nickname, age, score }) => {
  return (
    <div>
      <span className='User_data_text'>Name: {name}</span>
      <span className='User_data_text'>Surname: {surname}</span>
      <div className='Nick'>
        <span className='User_data_Nick_text'>Nickname: {nickname}</span>
        <button type="button" className="User_data_Nick_edit" onClick={handleClick}>
          <img src={EditPencil} alt="edit" className="User_data_Nick_edit" />
        </button>
      </div>
      </div>
      )
      }
