import React from "react";
import Gold from './Gold.png';
import Silver from './Silver.png';
import Bronze from './Bronze.png';
import useToken from "../useToken.js";

let index = 0;
let timer;
let Ind;

function get_data_from_server(token){
  const headers = {
    Authorization: `Bearer ${token}`,
  };
  axios.get("https://your-server.com/api/users/me", { headers })
    .then((response) => {
      const data = JSON.parse(response.data);

      //get user`s data from server
      user_id = data.id;
     
    })
    .catch((error) => {
      console.log(error);
    });
  }

export default function Item({ row }) {
  const {token} = useToken();

  if(token){

  }

  // Start timer
  if (!timer) {
    timer = setInterval(() => {
      index = 0;
    }, 2000); // 2 seconds
  }  

  index++;
  let medalImage = "";      //image for winners
  if (index / 2 === 1) {
    medalImage = Gold;
    Ind = <img src={medalImage} alt="Medal" className="Medal" />;
  } else if (index / 2 === 2) {
    medalImage = Silver;
    Ind = <img src={medalImage} alt="Medal" className="Medal" />;
  } else if (index / 2 === 3) {
    medalImage = Bronze;
    Ind = <img src={medalImage} alt="Medal" className="Medal" />;
  } else {
    Ind = index / 2;
  }



  return (
    <li className="item">
      <span className="item__index">{Ind}</span>
      <span className="item__nickname">{row.username}</span>
      <span className="item__score">{row.score}</span>
      <span className="item__time">{row.time}</span>
    </li>
  );
}
