import React from "react";
import Gold from './Gold.png';
import Silver from './Silver.png';
import Bronze from './Bronze.png';
import{getID} from '../User_account/User_account';

let index = 0;
let timer;
let Ind;
let id = getID();
console.log(id);

export default function Item({ row }) {
  // Start timer
  if (!timer) {
    timer = setInterval(() => {
      index = 0;
    }, 2000); // 2 seconds
  }  

  index++;
  let medalImage = "";
  if (index / 2 === 1) {
    medalImage = Gold;
    Ind = <img src={medalImage} alt="Medal" className="Medal" />;
  } else if (index / 2 === 2) {
    medalImage = Silver;
    Ind = <img src={medalImage} alt="Medal" className="Medal" />;
  } else if (index / 2 === 3) {
    medalImage = Bronze;
    Ind = <img src={medalImage} alt="Medal" className="Medal" />;
  } else {
    Ind = index / 2;
  }


  const isUserItem = row.id === id;
  const className = isUserItem ? "item_u" : "item";
  // const className_nickname = isUserItem ? "item__nickname_u" : "item__nickname";
  // const className_score = isUserItem ? "item__score_u" : "item__score";
  // const className_time = isUserItem ? "item__time_u" : "item__time";


 
    
    // <li className={className}>
    //   <span className={className_nickname}>{row.username}</span>
    //   <span className={className_score}>{row.score}</span>
    //   <span className={className_time}>{row.time}</span>
    // </li>

  return (
    <li className={className}>
      <span className="item__index">{Ind}</span>
      <span className="item__nickname">{row.firstName}</span>
      <span className="item__score">{row.age}</span>
      <span className="item__time">{row.id} min</span>
    </li>
  );
}
